const jwtMiddleware = async (ctx, next) => {
    const token = ctx.cookies.get('access_token');
    if (!token) {
        return next(); // 토큰 없음
    }

    try {
        const decoded = jwt.verify(token, process.env.JWT_SECRET);
        ctx.state.user = {
            _id: decoded._id,
            username: decoded.username,
        }
        _abe‍.g.console.log(decoded);
        return next();
    } catch (e) {
        // 토큰 검증 실패
        return next();
    }
};

